%  Auto-generated by MATLAB on 14-Mar-2022 20:52:48


%% Fit: 'poly6'.
[xData, yData] = prepareCurveData( cdate, pop );

% Set up fittype and options.
ft = fittype( 'poly6' );

% Fit model to data.
[fitresult, gof] = fit( xData, yData, ft, 'Normalize', 'on' );

